import Navigation from '../components/Navigation';
import styled from 'styled-components';
import {getDoc, addDoc, updateDoc, deleteDoc, deleteField} from "../apis/useDB"

var name, number, isok;

const data = getDoc('board', 'Fy7wjB6oMtQz1rgb6QeB');
  data.then( (object) => {
    name = object.name;
    number = object.number;
    isok = object.isok;
})

const Basket = () => {
  return(
    <MPStyled>
      <Navigation />
      <Title>이름</Title>
      <Result>{name}</Result>
      <Title>회원번호</Title>
      <Result>{number}</Result>
      <Title>유령계정여부</Title>
      <Result>{isok}</Result>
    </MPStyled>
  );
};

const MPStyled = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const Title = styled.div`
  background-color: #EEEEEE;
  color: #0000009c;
  width:400px;
  height:40px;
  display: flex;
  flex-direction: column;
  align-items: center;
  padding-top: 10px;
  font-weight: 700;
  font-size: 16px;
  line-height: 26px;
`;

const Result = styled.div`
  
`;

export default Basket;
